mapper.select:
SELECT partner, notify_url, out_trade_no, subject, product_code, total_fee, seller_id, seller_email, buyer_id, buyer_email, operator_code, operator_id, body, show_url, currency, price, quantity, goods_detail, extend_params, it_b_pay, royalty_type, royalty_parameters, channel_parameters, dynamicid_type, dynamic_id, remark, error, detail_error_code, detail_error_des, result_code, trade_no, trade_status, refund_code, refund_time, cancel_code, cancel_time, machine_code, cashier, refund_fee, disabled, createuserid, createusername, createusercode, createdatetime, createip, updateuserid, updateusername, updateusercode, updatedatetime, updateip, id, brandid, ouid, storecode, orgcode FROM ESALE_MOBILEPAY_ORDER
1=1
AND id = #{id}
AND ouid = #{ouid}
AND brandId = #{brandid}
AND storecode = #{storecode}
AND orgcode = #{orgcode}
AND out_trade_no = #{out_trade_no}
AND trade_no = #{trade_no}

SELECT o.OUT_TRADE_NO, o.TRADE_NO, date_format(o.createDatetime,'%Y-%c-%d %h:%i:%s') createdatetime, o.createUsername as cashier, o.TOTAL_FEE, '支付宝支付' as payMehod, s.storename as storeName, case when o.refund_code = 'SUCCESS' then '退款成功' when o.trade_status = 'TRADE_SUCCESS' then '交易成功' when o.trade_status = 'TRADE_CLOSED' then '交易关闭' when o.trade_status = 'TRADE_FINISHED' then '交易成功且关闭' when o.result_code = 'ORDER_FAIL' then '下单失败' when o.result_code ='ORDER_SUCCESS_PAY_SUCCESS' then '下单成功并且支付成功' when o.result_code ='ORDER_SUCCESS_PAY_FAIL' then '下单成功支付失败' when o.result_code ='ORDER_SUCCESS_PAY_INPROCESS' then '下单成功支付处理中' when o.result_code ='SUCCESS' then '预下单成功' when o.result_code ='FAIL' then '预下单失败' when o.result_code ='UNKNOWN' then '处理结果未知' else o.result_code ='异常订单' end as result_code
FROM ESALE_MOBILEPAY_ORDER o LEFT JOIN ESALES_STORES_INFO s ON o.ouId = s.OUID AND o.brandId = s.brandId AND s.STORECODE = o.STORECODE AND s.ORGCODE = o.ORGCODE
1=1
and o.out_trade_no=#{out_trade_no}
and o.trade_no=#{trade_no}
and o.createUserCode=#{casher}
and o.STORECODE=#{store}
and to_days(o.createDatetime)>=to_days(#{begin_time})
and to_days(o.createDatetime)<=to_days(#{end_time})
order by o.createDatetime desc limit #{offset} , #{limit}

SELECT count(1) as totalNum FROM ESALE_MOBILEPAY_ORDER o LEFT JOIN ESALES_STORES_INFO s ON o.ouId = s.OUID AND o.brandId = s.brandId AND s.STORECODE = o.STORECODE AND s.ORGCODE = o.ORGCODE
1=1
and o.out_trade_no=#{out_trade_no}
and o.trade_no=#{trade_no}
and o.createUserCode=#{casher}
and o.STORECODE=#{store}
and to_days(o.createDatetime)>=to_days(#{begin_time})
and to_days(o.createDatetime)<=to_days(#{end_time})

SELECT SUM(o.TOTAL_FEE) as totalFee FROM ESALE_MOBILEPAY_ORDER o LEFT JOIN ESALES_STORES_INFO s ON o.ouId = s.OUID AND o.brandId = s.brandId AND s.STORECODE = o.STORECODE AND s.ORGCODE = o.ORGCODE
o.result_code ='ORDER_SUCCESS_PAY_SUCCESS'
and o.out_trade_no=#{out_trade_no}
and o.trade_no=#{trade_no}
and o.createUserCode=#{casher}
and o.STORECODE=#{store}
and to_days(o.createDatetime)>=to_days(#{begin_time})
and to_days(o.createDatetime)<=to_days(#{end_time})
